    <div class=\"row justify-content-center align-items-center\">
        <div class=\"col-md-6\">
            <h2 class=\"mb-4 text-center\">Confirmer la réservation</h2>
            {{ form_start(form) }}
            <div class=\"form-group\">
                {{ form_label(form.prixreservation, 'Prix de la location :') }}
                {{ form_widget(form.prixreservation, { 'attr': {'class': 'form-control', 'readonly': true} }) }}
            </div>
            <div style=\"display:none;\">
                {{ form_widget(form.datedebut) }}
                {{ form_widget(form.datefin) }}
                {{ form_widget(form.utilisateur) }}
                {{ form_widget(form.itineraire) }}
                {{ form_widget(form.vehicule) }}
            </div>
            <div class=\"text-center\">
            <a href=\"{{ path('app_reservation_show', {'idreservation': reservation.idreservation}) }}\"  >{{ button_label|default('Confirmer') }}</button>
            </div>
            {{ form_end(form) }}
        </div>
    </div>
</div>
{% endblock %}
