                <td>{{ reclamation.contenu }}</td>
                <td>{{ reclamation.date ? reclamation.date|date('Y-m-d') : '' }}</td>
                <td>
                    <a href=\"{{ path('app_reclamation_show', {'idreclamation': reclamation.idreclamation}) }}\">Afficher</a>
